function dashboardSymbol($name, $alt = '', $class = '') {
    if (!empty($alt)) {
        $alt = 'alt="'.htmlspecialchars($alt).'" ';
    }
    $r = <<<EOT
<svg {$alt}class="icon $class icon-$name" viewBox="0 0 17 17"><use xlink:href="#$name" /></svg>
EOT;
    return $r;
}